const publicKeyN = ``

const publicKeyE = 0x10001

func TestParsePublicKey(t *testing.T) {
	key, err := ParsePublicKey(publicKey)
	if err != nil {
		t.Fatal(err)
	}

	if key.E != publicKeyE {
		t.Errorf("E == %d, expected %d", key.E, publicKeyE)
	}

	nbytes, err := hex.DecodeString(publicKeyN)
	if err != nil {
		t.Fatal(err)
	}
	n := new(big.Int)
	n.SetBytes(nbytes)
	if key.N.Cmp(n) != 0 {
		t.Errorf("N == %v, expected %v", key.N, n)
	}
}
